<?php

namespace Audero\ShowphotoBundle\Repository;

use Audero\ShowphotoBundle\Entity\PhotoRequest;
use Doctrine\ORM\EntityRepository;

/**
 * PhotoResponseRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PhotoResponseRepository extends EntityRepository
{

    /**
     * @param PhotoRequest $request
     * @return array
     */
    public function findBestResponses(PhotoRequest $request) {
        $result = $this->getEntityManager()
            ->createQuery("SELECT res as response, res.likes - res.dislikes as rating
                           FROM AuderoShowphotoBundle:PhotoResponse res
                           WHERE res.request = ?1
                           ORDER BY rating")
            ->setParameter(1, $request)
            ->getResult();

            // TODO FIX
            return !is_null($result[0]['rating']) ? $result : array();
    }
}
